#include <stdio.h>
#include <string.h>

int main() {
	char *a = "";
	int b, c, d, e, f, max = 0, prod, next_val, i;
	char b_str[2], c_str[2], d_str[2], e_str[2], f_str[2], g_str[2];
	int len = strlen(a);
	char test = '2';
	printf("%d\n", atoi(&test));
//	printf("%c", *(a + 1));
	strncpy(b_str, a, 1);
	strncpy(c_str, a + 1, 1);
	strncpy(d_str, a + 2, 1);
	strncpy(e_str, a + 3, 1);
	strncpy(f_str, a + 4, 1);
	b_str[1] = '\0';
	c_str[1] = '\0';
	d_str[1] = '\0';
	e_str[1] = '\0';
	f_str[1] = '\0';

	printf("%s %s %s %s %s\n", b_str, c_str, d_str, e_str, f_str);

	b = atoi(&b_str);
	c = atoi(&c_str);
	d = atoi(&d_str);
	e = atoi(&e_str);
	f = atoi(&f_str);

	printf("%d %d %d %d %d\n", b, c, d, e, f);
	prod = b * c * d * e * f;
	max = prod;
	for(i = 5; i <= len - 5; i ++) {
		strncpy(b_str, a + i, 1);
		strncpy(c_str, a + i - 5, 1);
		b_str[1] = '\0';
		c_str[1] = '\0';
		if(atoi(&c_str) != 0)
		prod = prod / atoi(&c_str) * atoi(&b_str);
		else {
			strncpy(d_str, a + i - 1, 1);
			strncpy(e_str, a + i - 2, 1);
			strncpy(f_str, a + i - 3, 1);
			strncpy(g_str, a + i - 4, 1);
			d_str[1] = '\0';
			e_str[1] = '\0';
			f_str[1] = '\0';
			g_str[1] = '\0';
			prod = atoi(&b_str) * atoi(&d_str) * atoi(&e_str) * atoi(&f_str) * atoi(&g_str);
		}
		printf("%d ", prod);
		if(prod > max) max = prod;
	}
	printf("%d\n", max);
	return 0;
}
